package ProjectEuler;



import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.StringTokenizer;

/**
 * @author Aakansha Doshi
 *
 */
public class LargestProductSeries {

	public static void main(String[] args) throws NumberFormatException, IOException {
		// TODO Auto-generated method stub
		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));	
		int T=Integer.parseInt(br.readLine().trim());
		
		String s;
		StringTokenizer st;
		while(T>0)
		{
			if((s=br.readLine().trim())!=null)
			{
				st=new StringTokenizer(s);
				int N=Integer.parseInt(st.nextToken());
				int K=Integer.parseInt(st.nextToken());
				String Num=" 821663704844031998900088952434506585412275886668811642717147992444292823086346567481391912316282458617866458359124566529476545682848912883142607690042 242190226710556263211111093705442175069416589604080719840385096245544436298123098787992724428490918884580156166097919133875499200524063689912560717606058861164671094050775410022569831552000559357297257163626956188267042825248360082325753042075296345";
				if(Num!=null){					
				long max=Long.MIN_VALUE;
				for(int i=0;i+K<N;i++)
				{
					String sub=Num.substring(i,i+K);
					long pro=1l;
					for(int j=0;j<sub.length();j++)
					{
						long ch=sub.charAt(j)-48;
						pro*=ch;
					}
					if(pro>max)
						max=pro;
				}
				System.out.println(max);
				T--;
				}
			}
			
		}
	}

}
